import { useEffect, useState } from "react";
import { Button, Card, Col, Container, Row } from "react-bootstrap";
import styles from "./Pedidos.module.css";

const Pedidos = () => {
  const [pedidos, setPedidos] = useState();

  const fetchAppointments = async () => {
    const response = await fetch("/pedidos/getPedidos");
    const json = await response.json();
    console.log(json);
    setPedidos(json);
    // if (Object.keys(json).length === 0) {
    //   setHasLoaded(false);
    // } else {
    //   setHasLoaded(true);
    // }
    console.log(pedidos);
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  return (
    <Container fluid className={styles.wrapper}>
      <h1>Pedidos</h1>

      {pedidos.map((pedido) => {
        return (
          <Row className={`${styles.infoContainer} col-12 px-0`}>
            <Col md={6} className={`${styles.title} p-2`}>
              <h3 className="text-center">Información del Pedido</h3>
            </Col>
            <Col md={6}>
              <h3 className="text-center">Estado del Pedido</h3>
              <div className={styles.icons}>
                <div
                  className={`${styles.iconContainer} ${styles.iconRealizado}`}
                >
                  <img src="/img/box.svg" alt="box"></img>
                  <p className="text-center">Realizado</p>
                </div>
                <div className={styles.iconContainer}>
                  <img
                    // style={{ backgroundColor: "yellow" }}
                    src="/img/enviado.svg"
                    alt="enviado"
                  />
                  <p className="text-center">Enviado</p>
                </div>
                <div className={styles.iconContainer}>
                  <img src="/img/entregado.svg" alt="entregado" />
                  <p className="text-center">Entregado</p>
                </div>
              </div>
            </Col>
          </Row>
        );
      })}

