import { useEffect, useRef, useState } from "react";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { APICitas, APIProducts, IMG_URL } from "../api/api";

const Alerts = (message, variant) => {
  return <Alert variant={variant}>{message}</Alert>;
};

const CustomModal = ({
  show,
  handleClose,
  appointments,
  pickAppointment,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const [dates, setDates] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const isMountedRef = useRef(true);

  const [formData, setFormData] = useState({
    fecha: pickAppointment.appointment_date.split(" ")[0],
    hora: pickAppointment.appointment_date
      .split(" ")
      .slice(1)
      .toString()
      .replace(",", " "),
    desc: pickAppointment.descripcion,
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    console.log(res);
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Cita modificada correctamente.", "success");
    }
  };

  const updateAppointment = async () => {
    const res = await APICitas.updateCita({
      fecha: formData.fecha,
      hora: formData.hora,
      descripcion: formData.desc,
      appointmentId: pickAppointment.appointment_id,
    });
    checkResponse(res);
  };

  const getCitas = async () => {
    const res = await APICitas.getDates();
    if (isMountedRef.current) {
      setDates(res);
      setHasLoaded(true);
    }
  };

  useEffect(() => {
    getCitas();
    return () => {
      // setDates({});
      // setHasLoaded({});
      isMountedRef.current = false;
    };
  }, []);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formFecha">
            <Form.Label>Fecha</Form.Label>
            <Form.Control
              value={formData.fecha}
              onChange={handleChange}
              name="fecha"
              type="date"
              placeholder="Fecha"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Label>Hora de la Cita</Form.Label>
            <Form.Control
              as="select"
              name="hora"
              value={formData.hora}
              onChange={handleChange}
            >
              <option disabled value={-1}>
                {pickAppointment.appointment_date
                  .split(" ")
                  .slice(1)
                  .toString()
                  .replace(",", " ")}
              </option>
              <option>10:00 am</option>
              <option>11:00 am</option>
              <option>12:00 pm</option>
              <option>13:00 pm</option>
              <option>14:00 pm</option>
              <option>15:00 pm</option>
              <option>16:00 pm</option>
              <option>17:00 pm</option>
              <option>18:00 pm</option>
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Desccripción</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Descripción"
              name="desc"
              value={formData.desc}
              onChange={handleChange}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={updateAppointment}>
          Editar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

