import React, { useEffect, useState } from "react";
import { Container, Table } from "react-bootstrap";
import styles from "./GestionProducts.module.css";

const GestionsProducts = () => {
  const [products, setProducts] = useState();

  const getProducts = async () => {
    const res = await fetch("/products/getProducts");
    const json = await res.json();
    setProducts(json);
  };

  useEffect(() => {
    getProducts();
    // console.log(products);
  }, []);

  return (
    <Container fluid className={styles.container}>
      <h1 className="text-center pt-3">Gesti√≥n de Animales</h1>
      <div className={styles.tableCont}>
        <Table striped className="" responsive>
          <thead className={styles.tableHead}>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Marca</th>
              <th>Precio</th>
              <th>Stock</th>
              <th>Descripcion</th>
              <th>Modificar</th>
              {/* {Array.from({ length: 12 }).map((_, index) => (
                <th key={index}>Table heading</th>
              ))} */}
            </tr>
          </thead>
          <tbody>
            {products
              ? Object.keys(products).map((item) => {
                  return (
                    <tr>
                      <td>{products[item].product_id}</td>
                      <td>{products[item].name}</td>
                      <td>{products[item].marca}</td>
                      <td>{products[item].price}</td>
                      <td>{products[item].cantidad}</td>
                      <td>{products[item].descripcion}</td>
                      <td>
                        <div className={styles.btnContainer}>
                          <div className={styles.editIcon}>
                            <box-icon name="edit" type="solid"></box-icon>
                          </div>
                          <div className={styles.deleteIcon}>
                            <i class="bx bxs-trash-alt"></i>
                            {/* <box-icon name="trash-alt" type="solid"></box-icon> */}
                          </div>
                        </div>
                      </td>
                    </tr>
                  );
                })
              : console.log("no")}
            {/* {products
              ? Object.keys(products).map((item) => {
                  return (
                    <Col
                      key={products[item].product_id}
                      lg={3}
                      className={styles.box}
                    >
                      <img
                        src={`${IMG_URL}${products[item].img}`}
                        alt={`producto${parseInt(item) + 1}`}
                      />
                      <div className={styles.content}>
                        <div className={styles.stars}></div>
                        <h3>{products[item].name}</h3>
                        {products[item].descripcion}
                        <div className={styles.price}>
                          {products[item].price} <span>$70.99</span>{" "}
                        </div>
                      </div>
                    </Col>
                  );
                })
              : console.log("Loading")} */}
            {/* <tr>
              <td>1</td>
              {Array.from({ length: 12 }).map((_, index) => (
                <td key={index}>Table cell {index}</td>
              ))}
            </tr>
            <tr>
              <td>2</td>
              {Array.from({ length: 12 }).map((_, index) => (
                <td key={index}>Table cell {index}</td>
              ))}
            </tr>
            <tr>
              <td>3</td>
              {Array.from({ length: 12 }).map((_, index) => (
                <td key={index}>Table cell {index}</td>
              ))}
            </tr> */}
          </tbody>
        </Table>
      </div>
    </Container>
  );
};

export default GestionsProducts;
