import React, { useContext, useEffect, useState } from "react";
import { Button, Card, Col, Container, Modal, Row } from "react-bootstrap";
import { SidebarContext } from "../../components/BaseAccountPage";
import { CustomModal } from "../../components/FormUtilities";
import styles from "./AccountPage.module.css";

const myModal = (show, handleClose) => {
  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

const CitaAccountPage = () => {
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);

  const handleShow = () => {
    setShow(true);
    // CustomModal(show, handleClose);
    myModal(show, handleClose);
    // <Modal show={show} handleClose={handleClose} />;
  };

  const { sidebar, setSidebar } = useContext(SidebarContext);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    setAppointment(json);
    setHasLoaded(true);
  };

  useEffect(() => {
    fetchAppointments();
    setSidebar(true);
  }, [setSidebar]);

  return (
    <Container fluid>
      <h1
        className={
          sidebar
            ? `col-md-8 ml-sm-auto col-lg-9 col-xl-10 p-0 ${styles.title}`
            : `${styles.title} col-12 p-0`
        }
      >
        Citas
      </h1>
      <Row
        className={
          sidebar
            ? `col-md-9 ml-sm-auto col-lg-10 col-xl-10 p-0 ${styles.infoContainer}`
            : `${styles.infoContainer} col-12 p-0`
        }
      >
        {hasLoaded ? (
          Object.keys(appointment).map((i, cont) => {
            return (
              <Col md={4} className="p-2" key={appointment[i].appointment_id}>
                <Card className={styles.card}>
                  <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                  <Card.Body>
                    <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                    <Card.Text>{`Descripción: ${appointment[i].descripcion}`}</Card.Text>
                  </Card.Body>
                  <Row className={styles.rowContainer}>
                    <Button variant="warning" size="lg" onClick={handleShow}>
                      Editar
                    </Button>
                    <Button variant="danger" size="lg">
                      Eliminar
                    </Button>
                  </Row>
                </Card>
              </Col>
            );
          })
        ) : (
          <p>asdasd</p>
        )}
      </Row>
    </Container>
  );
};

export default CitaAccountPage;
